import React, {useState, useRef} from 'react';
import './forca.css'
const words = require('./sub/words.json')

export const Forca = () => {
  const [word, setWord] = useState('')
  const [letter, setLetter] = useState('')
  const [value, setValue] = useState(letter)
  const [chances, setChances] = useState(5)
  const space = document.getElementsByClassName('letter')

  const random = (max, min) => {
    return Math.floor(Math.random() * (max - min + 1) + min)
  }

  const generate = () => {
    setWord(words.fiveLetterWords[random(0, (words.fiveLetterWords.length - 1))])
  }

  const check = () => {
    console.log('check called')
    const splited = word.split('')
    for(let i = 0; i < word.length; i++) {
      if(letter === splited[i]) {
        space[i].innerHTML = letter
      } else {
        setChances(chances - 1)
      }
    }
    setLetter('')
    setValue('')
  }

  return (
    <div className='container'>
      <div className='word'>
        <button className='letter'></button>
        <button className='letter'></button>
        <button className='letter'></button>
        <button className='letter'></button>
        <button className='letter'></button>
      </div>
      <p>Chances: {chances}</p>
      <input value={letter} disabled={letter.length >= 1?true:false} type="text" onChange={(e) => setLetter(e.target.value)}  />
      <button className='forca-button' onClick={() => check()}>send</button>
      <button  onClick={() => generate()}>generate</button>
      {word}
    </div>
  )
}
